/* eslint-disable no-template-curly-in-string */
import React, { useState,useEffect } from 'react';
import './App.css';
import Axios from 'axios';
import * as ReactBootstrap from "react-bootstrap"; 

function App() {

  const [SN, setSN]=useState('')
  const[stockName, setStockName]= useState('')
  const[transactionType, setTransactionType]= useState('')
  const[quantity, setQuantity]= useState('')
  const[amount, setAmount]= useState('')
  const[transactionDate, setTransactionDate]= useState('')

  const[portfolio, setPortfolio]=useState([]);

  useEffect(()=>{
    Axios.get("http://localhost:9000/api/get").then((Response)=>{
      setPortfolio(Response.data)
    });
  });


  const addPortfolio = () =>{
    Axios.post("http://localhost:9000/api/save",{stockName:stockName, transactionType:transactionType,
     quantity:quantity,amount:amount,transactionDate:transactionDate}).then(()=>{
       alert('success');
       reloadPage();
     });
  };

  const deletePortfolio =(SN)=>{
    Axios.delete('http://localhost:9000/api/delete/${SN}');
    reloadPage();
  }

  function reloadPage(){ 
    window.location.reload(); 
  }

  const renderportfolio = (portfolio,index)=>{
    return(
    <tr key={index}>
      <td>{portfolio.SN}</td>
      <td>{portfolio.StockName}</td>
      <td>{portfolio.TransactionType}</td>
      <td>{portfolio.Quantity}</td>
      <td>{portfolio.Amount}</td>
      <td>{portfolio.TransactionDate}</td>
      <td><button>Edit</button><button onClick={()=>deletePortfolio(portfolio.SN), SN.setSN=portfolio.SN}>Delete</button></td>
    </tr>
    )
  }

  return (
      <div className="App">

        <h1>Portfolio Management</h1>
        <label>Stock Name : </label>
        <input type="text" name="stockName" onChange={(e)=>{
          setStockName(e.target.value)
        }}/><br/>
        <label>Transaction Type : </label>
        <select name="transactionType" onChange={(e)=>{
          setTransactionType(e.target.value)
        }}>
          <option value="" disabled selected="true" >choose here</option>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select><br/>
        <label>Quantity : </label>
        <input type="number" name="quantity" onChange={(e)=>{
          setQuantity(e.target.value)
        }}/><br/>
        <label>Amount : </label>
        <input type="number" name="amount" onChange={(e)=>{
          setAmount(e.target.value)
        }}/><br/>
        <label>Transaction Date : </label>
        <input type="date" name="transactionDate" min="2015-01-01" max="2025-12-31" onChange={(e)=>{
          setTransactionDate(e.target.value)
        }}/><br/>

        <button onClick={addPortfolio}>Save</button>

        <table>
          <thead>
            <tr>
              <th>S.N.</th>
              <th>Stock Name</th>
              <th>Transaction Type</th>
              <th>Quantity</th>
              <th>Amount</th>
              <th>Transaction Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {portfolio.map(renderportfolio)}
          </tbody>
          </table>
            </div>
    );
 }


export default App;
