import AddPortfolio from './components/portfolio/AddPortfolio';
import EditPortfolio from './components/portfolio/EditPortfolio';

function App() {








  const deletePortfolio = (SN) => {
    Axios.delete('http://localhost:9000/api/delete/' + { SN }).then(() => {
      reloadPage();
    });

  }

  function reloadPage() {
    window.location.reload();
  }



  return (
    <Router>
      <div className="App">
        <Navbar />
        <Switch>
          <Route exact path="/" component = {Dashboard}/>
          <Route exact path="/portfolios" component={Portfolio}/>
          <Route exact path="/portfolio/add" component={AddPortfolio}/>
          <Route exact path="/portfolio/edit" component={EditPortfolio}/>
        </Switch> 
      </div>
    </Router>

