import React, { useState } from 'react';
import Axios from 'axios';
import {useHistory, useParams } from 'react-router-dom';
import Portfolio from '../pages/Portfolio';

const EditPortfolio = () => {
    
    const [portfolio, setPortfolio] = useState({
        stockName: "",
        transactionType: "",
        quantity: "",
        amount: "",
        transactionDate: ""
      });
 
    const{SN}=useParams();

    const { stockName, transactionType, quantity, amount, transactionDate } = portfolio;

    const onInputChange = e => {
        setPortfolio({ ...Portfolio, [e.target.name]: e.target.value });
      };
    
    let history = useHistory();

    const updatePortfolio = (e) => {
        e.preventDefault(); 
        Axios.post("http://localhost:9000/api/update", {
            stockName: stockName, transactionType: transactionType,
            quantity: quantity, amount: amount, transactionDate: transactionDate
        }).then(() => {
            alert('success');
            history.push("/portfolios");
        });
    };
    
    const loadportfolio = async () => {
        const result = await axios.get(`http://localhost:3003/get/${SN}}`);
        setPortfolio(result.data);
    };

    return ( 
        <div className="form-group">
            <h1>Edit Portfolio</h1>
            <label>Stock Name : </label>
            <input type="text" name="stockName" value={stockName} onInputChange={(e) => {
                (e.target.value)
            }} /><br />
            <label>Transaction Type : </label>
            <select name="transactionType" value={transactionType}  onChange={(e) =>onInputChange(e)}>
                <option value="" disabled selected="true" >choose here</option>
                <option value="buy">Buy</option>
                <option value="sell">Sell</option>
            </select><br />
            <label>Quantity : </label>
            <input type="number" name="quantity" value={quantity}  onChange={(e) =>onInputChange(e)}/><br />
            <label>Amount : </label>
            <input type="number" name="amount" value={amount} onChange={(e) =>onInputChange(e)} /><br />
            <label>Transaction Date : </label>
            <input type="date" name="transactionDate"  min="2015-01-01" max="2025-12-31" value={transactionDate} onChange={e => 
            onInputChange(e)} /><br />

            <button onClick={updatePortfolio}>Save</button>

        </div>
    );
}
export default EditPortfolio;
