//import React from "react";
import React, { useState, useEffect } from 'react';
import Axios from 'axios';



const [portfolio, setPortfolio] = useState([]);
const renderportfolio = (portfolio, index) => {
    return (

      <tr key={index}>
        <td>{portfolio.SN}</td>
        <td>{portfolio.StockName}</td>
        <td>{portfolio.TransactionType}</td>
        <td>{portfolio.Quantity}</td>
        <td>{portfolio.Amount}</td>
        <td>{portfolio.TransactionDate}</td>
        <td><button>Edit</button><button onClick={() => deletePortfolio(portfolio.SN)}>Delete</button></td>
      </tr>
    )
  }
  const deletePortfolio = (SN) => {
    Axios.delete('http://localhost:9000/api/delete/' + { SN }).then(() => {
      reloadPage();
    });

  }

  function reloadPage() {
    window.location.reload();
  }
const Portfolio=()=>{

    
    return (
        <div className="container">
            <div className="py-4">
                <h1>Portfolios</h1>
                <table>
        <thead>
          <tr>
            <th>S.N.</th>
            <th>Stock Name</th>
            <th>Transaction Type</th>
            <th>Quantity</th>
            <th>Amount</th>
            <th>Transaction Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {portfolio.map(renderportfolio)}
        </tbody>
      </table>
            </div>
        </div>
    )
}
export default Portfolio;
